import React from "react";
import { Col, Table, Button, Input } from 'reactstrap';
import { Progress } from "reactstrap";

class SelectedItems extends React.Component {
    constructor(props){
        super(props);
        this.state = { 
            item: '' , 
            calories: 0.0,
            totalFat: 0.0,
            satFat: 0.0,
            transFat: 0.0,
            protein: 0.0,
            carb: 0.0,
            calGoal: 2000
        };
        this.handleChange = this.handleChange.bind(this);
        this.inputCalories = this.inputCalories.bind(this);
        this.inputCalGoal = this.inputCalGoal.bind(this);
    }
    componentDidMount() {
        this.props.passRefUpward(this.refs);
    }
    handleChange(e) {
        this.setState({ item: e.target.textContent });
    }
    calorieColor() {
       if (this.state.calories > 200) {
            return "red";
        }else if(this.state.calories > 200) {

        } else {
            return "cyan";
        }
    }
    inputCalGoal(e) {
        this.setState({ calGoal: e.target.value });
    }
    inputCalories(e) {
        this.setState({ calories: e.target.value });
    }
    inputTotalFat(e) {
        this.setState({ totalFat: e.target.value });
    }
    inputSatFat(e) {
        this.setState({ satFat: e.target.value });
    }
    inputTransFat(e) {
        this.setState({ transFat: e.target.value });
    }
    inputProtein(e) {
        this.setState({ protein: e.target.value });
    }
    inputCarb(e) {
        this.setState({ carb: e.target.value });
    }

    render() {
        return (
            <Col className="selecteditems">
                <h2>Selected Items</h2>
                <select id="selected foods" size="8" ref="selected" onClick={this.handleChange}></select> 
                <h3>Calorie Goal: <Input ref="calories" type="number" placeholder={this.state.calGoal} onChange={this.inputCalGoal}></Input></h3>
                <Progress value={(parseFloat(this.state.calories) / parseFloat(this.state.calGoal)) * 100} color="danger"/>
                <Table className="nutrition">
                    <tbody>
                    <tr>
                        <th scope="row">Calories</th>
                        <td><Input style={{backgroundColor: this.calorieColor(),}} placeholder={this.state.calories} onChange={this.inputCalories}></Input></td>
                    </tr>
                    <tr>
                        <th scope="row">Total Fat</th>
                        <td><Input style={{backgroundColor: this.calorieColor(),}} placeholder={this.state.calories} onChange={this.inputCalories}></Input></td>
                    </tr>
                    <tr>
                        <th scope="row">Saturated Fat</th>
                        <td><Input style={{backgroundColor: this.calorieColor(),}} placeholder={this.state.calories} onChange={this.inputCalories}></Input></td>
                    </tr>
                    <tr>
                        <th scope="row">Trans Fat</th>
                        <td><Input style={{backgroundColor: this.calorieColor(),}} placeholder={this.state.calories} onChange={this.inputCalories}></Input></td>
                    </tr>
                    <tr>
                        <th scope="row">Protein</th>
                        <td><Input style={{backgroundColor: this.calorieColor(),}} placeholder={this.state.calories} onChange={this.inputCalories}></Input></td>
                    </tr>
                    <tr>
                        <th scope="row">Carbohydrate</th>
                        <td><Input style={{backgroundColor: this.calorieColor(),}} placeholder={this.state.calories} onChange={this.inputCalories}></Input></td>
                    </tr>
                    </tbody>
                </Table>
            </Col>
        );
    }
}

export default SelectedItems;
