        // el.textContent = values.options[values.selectedIndex].text;
        // el.value = values.value;
        // select.appendChild(el);


        // var totalCal = parseInt(cal.innerHTML);
        // var valueCal = parseInt(values.value);

        // totalCal += valueCal;

        // cal.innerHTML = totalCal;

    }

    removeFromSelected(values) {
        // var select = document.getElementById("selected foods");
        // var cal = document.getElementById("total calories");

        // var totalCal = parseInt(cal.innerHTML);
        // var valueCal = parseInt(values.value);

        // totalCal -= valueCal;

        // cal.innerHTML = totalCal;

        // select.removeChild(values.options[values.selectedIndex]);
    }

    handleclick() {
        console.log("button clicked");
        console.log(this.refs.food.getValue());
        // if (document.getElementById("selected foods").value !== "") {
        //     this.removeFromSelected(document.getElementById("selected foods"));
        // }else if (document.getElementById("food list").value !== "") {
        //     this.addToSelected(document.getElementById("food list"));
        // }

    }

    render() {
        return (
            <div className="addbutton">
                <Categories ref="food" onChange={() => this.setList()}/>
                <MenuItems ref="Menu"/>
                <input type="button" value = ">>" id="add button" onClick={() => this.setList()}/>
                <SelectedItems/>
            </div>
        );
    }
} 

export default AddButton;

ReactDOM.render(
    <div>
      <Headings/>
      <div class="flex">
        <div class="button"><AddButton /></div>
      </div>
    </div>,
    document.getElementById('root')
);
