import React from "react"

class SelectedItems extends React.Component {
    constructor(props){
        super(props);
        this.state = { item: '' , calories: '0'};
        this.handleChange = this.handleChange.bind(this);
        this.handleCalories = this.handleCalories.bind(this);
    }
    componentDidMount() {
        this.props.passRefUpward(this.refs);
    }
    handleChange(e) {
        this.setState({ item: e.target.textContent });
        console.log(this.state.item);
        this.setState({ calories: e.target.textContent });
        console.log(this.state.calories);
    }
    handleCalories(e) {
        this.setState({ calories: e.target.textContent });
        console.log(this.state.calories);
    }
    render() {
        return (
            <div className="selecteditems">
                <h2>Selected Items</h2>
                <select id="selected foods" size="8" ref="selected" onClick={this.handleChange}></select> 
                <h3>Total Calories: <span id="total calories" ref="calories" onClick={this.handleCalories}>0</span></h3>
            </div>
        );
    }
}

export default SelectedItems;
