                    var opt = fruits[i];
                    var cal = fruitsCalories[i];
                    break;
                case 'Vegitables':
                    var opt = vegitables[i];
                    var cal = vegitablesCalories[i];
                    break;
                case 'Dairy':
                    var opt = dairy[i];
                    var cal = dairyCalories[i];
                    break;
                case 'Grain':
                    var opt = grain[i];
                    var cal = grainCalories[i];
                    break;
                default:
                    break;
            }
            var el = document.createElement("option");
            el.textContent = opt;
            el.value = cal;
            console.log(opt);
            console.log(el.value);

            // select.appendChild(el);

        }
    }

