        // var select = document.getElementById("selected foods")
        // var el = document.createElement("option");
        // var cal = document.getElementById("total calories");

        // el.textContent = values.options[values.selectedIndex].text;
        // el.value = values.value;
        // select.appendChild(el);


        // var totalCal = parseInt(cal.innerHTML);
        // var valueCal = parseInt(values.value);

        // totalCal += valueCal;

        // cal.innerHTML = totalCal;

    }

    removeFromSelected(values) {
        // var select = document.getElementById("selected foods");
        // var cal = document.getElementById("total calories");

        // var totalCal = parseInt(cal.innerHTML);
        // var valueCal = parseInt(values.value);

        // totalCal -= valueCal;

        // cal.innerHTML = totalCal;

        // select.removeChild(values.options[values.selectedIndex]);
    }

    handleclick() {
        console.log("button clicked");
        console.log(this.refs.food.getValue());
