import React from "react"
import { Col, Table, Button } from 'reactstrap';

class MenuItems extends React.Component {
    constructor(props){
        super(props);
        this.state = { 
            fooditem: '',
            calories: '',
            totalFat: '',
            satFat: '',
            transFat: '',
            protein: '',
            carb: ''
        };
        this.handleChange = this.handleChange.bind(this);
    }
    componentDidMount() {
        this.props.passRefUpward(this.refs);
    }
    handleChange(e) {
        this.setState({ fooditem: e.target.textContent });
        this.props.setNutritional();
    }

    render() {
        return (
            <Col className="menuitems">
                <h2>Menu Items</h2>
                <select id="food list" ref="menu"size="5" onClick={this.handleChange}></select>
                <h3>Nutritional Info</h3>
                <Table className="nutrition">
                    <tbody>
                    <tr>
                        <th scope="row">Calories</th>
                        <Button ref="calories" color="primary">{this.state.calories}</Button>
                    </tr>
                    <tr>
                        <th scope="row">Total Fat</th>
                        <td ref="totalFat">{this.state.totalFat}</td>
                    </tr>
                    <tr>
                        <th scope="row">Saturated Fat</th>
                        <td ref="satFat">{this.state.satFat}</td>
                    </tr>
                    <tr>
                        <th scope="row">Trans Fat</th>
                        <td ref="transFat">{this.state.transFat}</td>
                    </tr>
                    <tr>
                        <th scope="row">Protein</th>
                        <td>{this.state.protein}</td>
                    </tr>
                    <tr>
                        <th scope="row">Carbohydrate</th>
                        <td>{this.state.carb}</td>
                    </tr>
                    </tbody>
                </Table>
            </Col>
        );
    }
}

export default MenuItems;
