import React from "react";
import { Col, Table, Button, Input } from 'reactstrap';
import { Progress } from "reactstrap";

class SelectedItems extends React.Component {
    constructor(props){
        super(props);
        this.state = { 
            item: '' , 
            calories: 0.0,
            totalFat: 0.0,
            satFat: 0.0,
            transFat: 0.0,
            protein: 0.0,
            carb: 0.0,
            calGoal: 2000
        };
        this.handleChange = this.handleChange.bind(this);
    }
    componentDidMount() {
        this.props.passRefUpward(this.refs);
    }
    handleChange(e) {
        this.setState({ item: e.target.textContent });
    }
    calorieColor() {
       if (this.state.calories > 100) {
            return "red";
        } else {
            return "cyan";
        }
    }
    testy() {console.log("ASDASD")}
    render() {
        return (
            <Col className="selecteditems">
                <h2>Selected Items</h2>
                <select id="selected foods" size="8" ref="selected" onClick={this.handleChange}></select> 
                <h3>Calorie Goal: <Button id="total calories" ref="calories" color={this.calorieColor}>{this.state.calGoal}</Button></h3>
                <Progress value={(parseFloat(this.state.calories) / parseFloat(this.state.calGoal)) * 100} color="danger"/>
                <Table className="nutrition">
                    <tbody>
                    <tr>
                        <th scope="row">Calories</th>
                        <Input ref="calories" style={{backgroundColor: this.calorieColor(),}} placeholder={this.state.calories} onChange={this.testy}></Input>
                    </tr>
                    <tr>
                        <th scope="row">Total Fat</th>
                        <td ref="totalFat">{this.state.totalFat}</td>
                    </tr>
                    <tr>
                        <th scope="row">Saturated Fat</th>
                        <td ref="satFat">{this.state.satFat}</td>
                    </tr>
                    <tr>
                        <th scope="row">Trans Fat</th>
                        <td ref="transFat">{this.state.transFat}</td>
                    </tr>
                    <tr>
                        <th scope="row">Protein</th>
                        <td>{this.state.protein}</td>
                    </tr>
                    <tr>
                        <th scope="row">Carbohydrate</th>
                        <td>{this.state.carb}</td>
                    </tr>
                    </tbody>
                </Table>
            </Col>
        );
    }
}

export default SelectedItems;
