import React from "react"


function SelectedItems() {
    return (
        <div className="selecteditems">
            <h2>Selected Items</h2>
            <select id="selected foods" size="8"></select> 
            <h3>Total Calories: <span id="total calories">0</span></h3>
        </div>
    );
}

function MenuItems() {
    return (
        <div className="menuitems">
            <h2>Menu Items</h2>
            <select id="food list" size="5"></select>
        </div>
    );
}
function Categories() {
    return (
        <div className="categories">
        <h2>Categories</h2>
        <select name="food types" id="food types">
            <option value="Protein" onClick={() => this.setList("Protein")}>Protein</option>
            <option value="Fruits" onClick={() => this.setList("Fruits")}>Fruits</option>
            <option value="Vegitables" onClick={() => this.setList("Vegitables")}>Vegitables</option>
            <option value="Dairy" onClick={() => this.setList("Dairy")}>Dairy</option>
            <option value="Grain" onClick={() => this.setList("Grain")}>Grain</option>
        </select>
        </div>
    );
}


class AddButton extends React.Component {
    constructor(props) {
        super(props)
        this.menuRef = React.createRef();
    }

    setList = (foodtype) => {
        let proteins = ["steak", "ground beef", "chicken", "fish", "soy"]
        let fruits = ["orange", "banana", "pineapple", "grapes", "blueberries"]
        let vegitables = ["romaine", "green beans", "squach", "spinach", "kale"]
        let dairy = ["milk", "yogurt", "cheddar cheese", "skim milk", "cottage cheese"]
        let grain = ["bread", "bagel", "pita", "naan", "tortilla"]

        let proteinsCalories = ["300", "200", "100", "80", "50"]
        let fruitsCalories = ["300", "200", "100", "80", "50"]
        let vegitablesCalories = ["30", "40", "100", "50", "10"]
        let dairyCalories = ["300", "200", "200", "100", "80"]
        let grainCalories = ["200", "300", "250", "210", "120"]

        var select = document.getElementById("food list")

        for(var i = 5; i >= 0; i--) {select.remove(i)}

        for(var i = 0; i < 5; i++) {
            switch (foodtype) {
                case 'Protein':
                    var opt = proteins[i];
                    var cal = proteinsCalories[i];
                    break;
                case 'Fruits':
                    var opt = fruits[i];
                    var cal = fruitsCalories[i];
                    break;
                case 'Vegitables':
                    var opt = vegitables[i];
                    var cal = vegitablesCalories[i];
                    break;
                case 'Dairy':
                    var opt = dairy[i];
                    var cal = dairyCalories[i];
                    break;
                case 'Grain':
                    var opt = grain[i];
                    var cal = grainCalories[i];
                    break;
                default:
                    break;
            }
            var el = document.createElement("option");
            el.textContent = opt;
            el.value = cal;

            select.appendChild(el);

        }
    }
    // addToSelected(values) {

    //     var select = document.getElementById("selected foods")
    //     var el = document.createElement("option");
    //     var cal = document.getElementById("total calories");

    //     el.textContent = values.options[values.selectedIndex].text;
    //     el.value = values.value;
    //     select.appendChild(el);


    //     var totalCal = parseInt(cal.innerHTML);
    //     var valueCal = parseInt(values.value);

    //     totalCal += valueCal;

    //     cal.innerHTML = totalCal;

    // }

    // removeFromSelected(values) {
    //     var select = document.getElementById("selected foods")
    //     var cal = document.getElementById("total calories");

    //     var totalCal = parseInt(cal.innerHTML);
    //     var valueCal = parseInt(values.value);

    //     totalCal -= valueCal;

    //     cal.innerHTML = totalCal;

    //     select.removeChild(values.options[values.selectedIndex]);
    // }

    handleclick() {
        // if (document.getElementById("selected foods").value !== "") {
        //     this.removeFromSelected(document.getElementById("selected foods"));
        // }else if (document.getElementById("food list").value !== "") {
        //     this.addToSelected(document.getElementById("food list"));
        // }

    }

    render() {
        return (
            <div className="addbutton">
                <Categories/>
                <MenuItems ref={this.menuRef}/>
                <input type="button" value = ">>" id="add button" onClick={() => this.handleclick()}/>
                <SelectedItems/>
            </div>
        );
    }
} 

export default AddButton;
