// import React from "react"
// import AddButton from "./AddButton";

class Categories extends React.Component {
    constructor(props) {
        super(props);
        this.state = { food: '' };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        this.setState({ food: e.target.value });
        console.log(this.state.food);

    }
    getValue () {
        return this.state.food;
    }

    render() {
        return (
            <div className="categories">
            <h2>Categories</h2>
            <select name="food types" id="food types" ref="Food types">
                <option value="Protein" onClick={this.handleChange}>Protein</option>
                <option value="Fruits" onClick={this.handleChange}>Fruits</option>
                <option value="Vegitables" onClick={this.handleChange}>Vegitables</option>
                <option value="Dairy" onClick={this.handleChange}>Dairy</option>
                <option value="Grain" onClick={this.handleChange}>Grain</option>
            </select>
            </div>
        );
    }
}


