// import './index.css';

import React from "react"

class SelectedItems extends React.Component {
    addToSelected(values) {

        var select = document.getElementById("selected foods")
        var el = document.createElement("option");
        var cal = document.getElementById("total calories");

        el.textContent = values.options[values.selectedIndex].text;
        el.value = values.value;
        select.appendChild(el);


        totalCal = parseInt(cal.innerHTML);
        valueCal = parseInt(values.value);

        totalCal += valueCal;

        cal.innerHTML = totalCal;

    }

    removeFromSelected(values) {
        var select = document.getElementById("selected foods")
        var cal = document.getElementById("total calories");

        totalCal = parseInt(cal.innerHTML);
        valueCal = parseInt(values.value);

        totalCal -= valueCal;

        cal.innerHTML = totalCal;

        select.removeChild(values.options[values.selectedIndex]);
    }

    render() {
        return (
            <div className="SelectedItems">
                <input type="button" value = ">>" id="add button"/>

                <h2>Selected Items</h2>
                <select id="selected foods" size="8"></select> 
                <h3>Total Calories: <span id="total calories">0</span></h3>
            </div>
        );
    }
} 

export default SelectedItems;
