import React from "react"
import Categories from "./Categories";
import MenuItems from "./MenuItems";
import SelectedItems from "./SelectedItems";
import AddButton from "./AddButton";
import { Row } from 'reactstrap';
import './index.css';
import MyComponent from "./mycomponent";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state={data: ""}

        this.Categories = React.createRef();
        this.Menu = React.createRef();
        this.Selected = React.createRef();

        this.getRefsFromMenu = this.getRefsFromMenu.bind(this);
        this.getRefsFromSelected = this.getRefsFromSelected.bind(this);
    }

    componentDidMount() {
        this.fetchData();
    }

    updateData = (apiResponse) => {
        console.log(apiResponse);
        this.setState({data: apiResponse});
        // this.props.menu.append(el);
    }

    fetchData = () => {
        fetch('/example_api')
        .then(
            (response) => {
                if (response.status === 200){return (response.json()) ;}
                else{
                    console.log("HTTP error:" + response.status + ":" +  response.statusText);
                    return ([ ["status ", response.status]]);
                }
            })//The promise response is returned, then we extract the json data
        .then ((jsonOutput) => {//jsonOutput now has result of the data extraction
            this.updateData(jsonOutput);
        })
        .catch((error) => {
            console.log(error);
                this.updateData("");
        })

    }

