// import './index.css';

function Categories() {
  return (
    <div className="Categories">
      <h2>Categories</h2>
      <select name="food types" id="food types">
        <option value="Protein" onclick="setList('Protein')">Protein</option>
        <option value="Fruits" onclick="setList('Fruits')">Fruits</option>
        <option value="Vegitables" onclick="setList('Vegitables')">Vegitables</option>
        <option value="Dairy" onclick="setList('Dairy')">Dairy</option>
        <option value="Grain" onclick="setList('Grain')">Grain</option>
      </select>
    </div>
  );
}

let proteins = ["steak", "ground beef", "chicken", "fish", "soy"]
let fruits = ["orange", "banana", "pineapple", "grapes", "blueberries"]
let vegitables = ["romaine", "green beans", "squach", "spinach", "kale"]
let dairy = ["milk", "yogurt", "cheddar cheese", "skim milk", "cottage cheese"]
let grain = ["bread", "bagel", "pita", "naan", "tortilla"]

let proteinsCalories = ["300", "200", "100", "80", "50"]
let fruitsCalories = ["300", "200", "100", "80", "50"]
let vegitablesCalories = ["30", "40", "100", "50", "10"]
let dairyCalories = ["300", "200", "200", "100", "80"]
let grainCalories = ["200", "300", "250", "210", "120"]


function setList(foodtype) {
    var select = document.getElementById("food list")

    for(var i = 5; i >= 0; i--) {select.remove(i)}

    for(var i = 0; i < 5; i++) {
        switch (foodtype) {
            case 'Protein':
                var opt = proteins[i];
                var cal = proteinsCalories[i];
                break;
            case 'Fruits':
                var opt = fruits[i];
                var cal = fruitsCalories[i];
                break;
            case 'Vegitables':
                var opt = vegitables[i];
                var cal = vegitablesCalories[i];
                break;
            case 'Dairy':
                var opt = dairy[i];
                var cal = dairyCalories[i];
                break;
            case 'Grain':
                var opt = grain[i];
                var cal = grainCalories[i];
                break;
            default:
                break;
        }
        var el = document.createElement("option");
        el.textContent = opt;
        el.value = cal;

        select.appendChild(el);

    }
} 

export default Categories;
