import React from "react"

class MenuItems extends React.Component {
    constructor(props){
        super(props);
        this.state = { fooditem: '' };
        this.handleChange = this.handleChange.bind(this);
    }
    componentDidMount() {
        this.props.passRefUpward(this.refs);
    }
    handleChange(e) {
        this.setState({ fooditem: e.target.textContent });
        console.log(this.state.fooditem);
        this.props.passRefUpward(this.state.fooditem);

    }
    getValue () {
        return this.state.fooditem;
    }
    render() {
        return (
            <div className="menuitems">
                <h2>Menu Items</h2>
                <select id="food list" ref="menu"size="5" onChange={this.handleChange}></select>
            </div>
        );
    }
}

export default MenuItems;
